export const setArticles = articles => ({
    type: 'SET_ARTICLES',
    payload: articles
});
export const setId = id => ({
  type: 'SET_ID',
  payload: id,
});
export const setArticlesSpeedway = articles => ({
  type: 'SET_ARTICLES_SPEEDWAY',
  payload: articles,
});
export const setArticlesTravel = articles => ({
  type: 'SET_ARTICLES_TRAVEL',
  payload: articles,
});